package com.virginkiss.time;

public class TimeImpl implements ITime {

	private int hour;
	private int minute;
	
	public TimeImpl(int hour, int minute) {
		// TODO Auto-generated constructor stub
		this.hour  = hour;
		this.minute = minute;
	}
	
	public int getHour() {
		// TODO Auto-generated method stub
		return hour;
	}

	public int getMinute() {
		// TODO Auto-generated method stub
		return minute;
	}

}
